import sys

def calculate(op, a, b):
    ops = {'+': a+b, '-': a-b, '*': a*b, '/': a/b}
    return ops.get(op, 'Unknown operator')

if __name__ == '__main__':
    a, op, b = float(sys.argv[1]), sys.argv[2], float(sys.argv[3])
    print(calculate(op, a, b))
